SELECT UPPER(db_vgnylyan.user_card.last_name) AS 'NAME', db_vgnylyan.user_card.first_name, db_vgnylyan.subscription.price FROM db_vgnylyan.user_card INNER JOIN db_vgnylyan.member ON db_vgnylyan.user_card.id_user=db_vgnylyan.member.id_user_card INNER JOIN db_vgnylyan.subscription ON db_vgnylyan.member.id_sub=db_vgnylyan.subscription.id_sub WHERE db_vgnylyan.subscription.price > 42 ORDER BY last_name ASC, first_name ASC;